import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [token, setToken] = useState('');
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [isTokenEntered, setIsTokenEntered] = useState(false);

  // Fetch paginated users
  const fetchUsers = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/users?page=${page}&limit=5`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data.data);
      setTotalPages(Math.ceil(response.data.total / 5)); // Assume 5 users per page
    } catch (error) {
      console.error('Error fetching users:', error.response ? error.response.data : error.message);
      alert(error.response?.data?.message || 'Failed to fetch users');
    }
  };

  // Delete user by ID
  const deleteUser = async (userId) => {
    try {
      await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('User deleted successfully');
      fetchUsers(); // Refresh user list after deletion
    } catch (error) {
      console.error('Error deleting user:', error.response ? error.response.data : error.message);
      alert(error.response?.data?.message || 'Failed to delete user');
    }
  };

  useEffect(() => {
    if (isTokenEntered) fetchUsers();
  }, [page, isTokenEntered]);

  const handleTokenChange = (e) => {
    setToken(e.target.value);
    setIsTokenEntered(e.target.value.trim() !== '');
  };

  const handlePageChange = (direction) => {
    if (direction === 'prev' && page > 1) setPage(page - 1);
    if (direction === 'next' && page < totalPages) setPage(page + 1);
  };

  return (
    <div>
      <h2>User List</h2>
      <input
        type="text"
        placeholder="Enter JWT Token"
        onChange={handleTokenChange}
        value={token}
        style={{ width: '300px', marginBottom: '10px' }}
      />
      <button
        onClick={fetchUsers}
        disabled={!isTokenEntered}
        style={{
          marginLeft: '10px',
          padding: '5px 10px',
          cursor: isTokenEntered ? 'pointer' : 'not-allowed',
          backgroundColor: isTokenEntered ? 'blue' : 'gray',
          color: 'white',
          border: 'none',
        }}
      >
        Fetch Users
      </button>
      <ul>
        {users.map((user) => (
          <li key={user._id} style={{ marginBottom: '10px' }}>
            <strong>{user.name} {user.lastName}</strong> - {user.email}
            <br />
            <span>User ID: {user._id}</span>
            <button
              onClick={() => deleteUser(user._id)}
              style={{
                marginLeft: '10px',
                padding: '5px 10px',
                backgroundColor: 'red',
                color: 'white',
                border: 'none',
                cursor: 'pointer',
              }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
      <div style={{ marginTop: '20px' }}>
        <button
          onClick={() => handlePageChange('prev')}
          disabled={page === 1}
          style={{
            marginRight: '10px',
            padding: '5px 10px',
            backgroundColor: page === 1 ? 'gray' : 'blue',
            color: 'white',
            border: 'none',
            cursor: page === 1 ? 'not-allowed' : 'pointer',
          }}
        >
          Previous
        </button>
        <button
          onClick={() => handlePageChange('next')}
          disabled={page === totalPages}
          style={{
            padding: '5px 10px',
            backgroundColor: page === totalPages ? 'gray' : 'blue',
            color: 'white',
            border: 'none',
            cursor: page === totalPages ? 'not-allowed' : 'pointer',
          }}
        >
          Next
        </button>
      </div>
      <p>Page {page} of {totalPages}</p>
    </div>
  );
};

export default UserList;
