import React, { useState } from 'react';
import axios from 'axios';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [token, setToken] = useState('');
  const [isTokenEntered, setIsTokenEntered] = useState(false); // Track if the token is entered

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/users', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data.data);
    } catch (error) {
      console.error('Error fetching users:', error.response ? error.response.data : error.message);
      alert(error.response?.data?.message || 'Failed to fetch users');
    }
  };

  const handleTokenChange = (e) => {
    setToken(e.target.value);
    setIsTokenEntered(e.target.value.trim() !== ''); // Enable the button only if token is entered
  };

  return (
    <div>
      <h2>User List</h2>
      <input
        type="text"
        placeholder="Enter JWT Token"
        onChange={handleTokenChange}
        value={token}
        style={{ width: '300px', marginBottom: '10px' }}
      />
      <button
        onClick={fetchUsers}
        disabled={!isTokenEntered} // Disable button if no token is entered
        style={{
          marginLeft: '10px',
          padding: '5px 10px',
          cursor: isTokenEntered ? 'pointer' : 'not-allowed',
          backgroundColor: isTokenEntered ? 'blue' : 'gray',
          color: 'white',
          border: 'none',
        }}
      >
        Fetch Users
      </button>
      <ul>
        {users.map((user) => (
          <li key={user._id} style={{ marginBottom: '10px' }}>
            <strong>{user.name} {user.lastName}</strong> - {user.email}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserList;
