import React, { useState } from 'react';
import axios from 'axios';

const CreateUser = () => {
  const [formData, setFormData] = useState({
    name: '',
    lastName: '',
    email: '',
    mobile: '',
    userImage: '',
    password: '',
  });

  const [token, setToken] = useState(''); // JWT token
  const [showToken, setShowToken] = useState(false); // Toggle token visibility

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/users', formData, {
        headers: {
          'Content-Type': 'application/json',
        },
      });
      alert('User created successfully');
      console.log('API response:', response.data);

      // Set and display the token temporarily
      setToken(response.data.token);
      setShowToken(true);
      setTimeout(() => {
        setShowToken(false); // Hide token after 5 seconds
      }, 5000);
    } catch (error) {
      console.error('Error creating user:', error.response ? error.response.data : error.message);
      alert(error.response?.data?.message || 'Failed to create user');
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(token);
    alert('Token copied to clipboard');
  };

  return (
    <div>
      <h2>Create User</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
        <input type="text" name="lastName" placeholder="Last Name" onChange={handleChange} required />
        <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
        <input type="text" name="mobile" placeholder="Mobile" onChange={handleChange} />
        <input type="text" name="userImage" placeholder="User Image URL" onChange={handleChange} />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
        <button type="submit">Create</button>
      </form>

      {showToken && (
        <div style={{ marginTop: '20px' }}>
          <h3>JWT Token (Visible for 5 seconds)</h3>
          <textarea
            value={token}
            readOnly
            rows="5"
            style={{ width: '100%', marginBottom: '10px' }}
          />
          <button onClick={copyToClipboard} style={{ backgroundColor: 'blue', color: 'white', padding: '5px 10px', border: 'none', cursor: 'pointer' }}>
            Copy Token
          </button>
        </div>
      )}
    </div>
  );
};

export default CreateUser;
