import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [token, setToken] = useState('');

  const fetchUsers = useCallback(async () => {
    try {
      const response = await axios.get('/api/users', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  }, [token]); // Add 'token' as a dependency for fetchUsers

  const deleteUser = async (userId) => {
    try {
      const response = await axios.delete(`/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('User deleted successfully');
      console.log('Delete response:', response.data);

      // Refresh the user list after deletion
      setUsers(users.filter((user) => user._id !== userId));
    } catch (error) {
      console.error('Error deleting user:', error.response ? error.response.data : error.message);
      alert('Failed to delete user');
    }
  };

  useEffect(() => {
    if (token) fetchUsers();
  }, [fetchUsers]); // Pass fetchUsers as a dependency

  const handleTokenChange = (e) => setToken(e.target.value);

  return (
    <div>
      <h2>User List</h2>
      <input
        type="text"
        placeholder="Enter JWT Token"
        onChange={handleTokenChange}
        value={token}
        style={{ width: '300px', marginBottom: '10px' }}
      />
      <ul>
        {users.map((user) => (
          <li key={user._id} style={{ marginBottom: '10px' }}>
            <strong>{user.name} {user.lastName}</strong> - {user.email}
            <button
              onClick={() => deleteUser(user._id)}
              style={{
                marginLeft: '10px',
                backgroundColor: 'red',
                color: 'white',
                border: 'none',
                padding: '5px 10px',
                cursor: 'pointer',
              }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UserList;
