import React, { useState } from 'react';
import axios from 'axios';

const GetToken = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState('');
  const [showToken, setShowToken] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/users/getToken', {
        email,
        password,
      });

      setToken(response.data.token);
      setShowToken(true);

      // Hide the token after 5 seconds
      setTimeout(() => {
        setShowToken(false);
      }, 5000);

      alert('Token fetched successfully');
    } catch (error) {
      console.error('Error fetching token:', error.response ? error.response.data : error.message);
      alert(error.response?.data?.message || 'Failed to fetch token');
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(token);
    alert('Token copied to clipboard');
  };

  return (
    <div>
      <h2>Get JWT Token</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          placeholder="Enter Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          style={{ marginBottom: '10px', display: 'block' }}
        />
        <input
          type="password"
          placeholder="Enter Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          style={{ marginBottom: '10px', display: 'block' }}
        />
        <button type="submit" style={{ padding: '10px', backgroundColor: 'blue', color: 'white', border: 'none', cursor: 'pointer' }}>
          Get Token
        </button>
      </form>

      {showToken && (
        <div style={{ marginTop: '20px' }}>
          <h3>JWT Token (Visible for 5 seconds)</h3>
          <textarea
            value={token}
            readOnly
            rows="5"
            style={{ width: '100%', marginBottom: '10px' }}
          />
          <button
            onClick={copyToClipboard}
            style={{
              padding: '5px 10px',
              backgroundColor: 'green',
              color: 'white',
              border: 'none',
              cursor: 'pointer',
            }}
          >
            Copy Token
          </button>
        </div>
      )}
    </div>
  );
};

export default GetToken;
