import React, { useState } from 'react';
import axios from 'axios';

const UpdateUser = () => {
  const [userId, setUserId] = useState('');
  const [formData, setFormData] = useState({
    name: '',
    lastName: '',
    email: '',
    mobile: '',
    userImage: '',
  });
  const [token, setToken] = useState('');
  const [isUserLoaded, setIsUserLoaded] = useState(false);

  // Fetch user details by ID
  const fetchUser = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      const { name, lastName, email, mobile, userImage } = response.data.data;
      setFormData({ name, lastName, email, mobile, userImage });
      setIsUserLoaded(true);
    } catch (error) {
      console.error('Error fetching user:', error.response ? error.response.data : error.message);
      alert(error.response?.data?.message || 'Failed to fetch user');
    }
  };

  // Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Submit updated user details
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`http://localhost:5000/api/users/${userId}`, formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('User updated successfully');
      console.log('Updated User:', response.data);
    } catch (error) {
      console.error('Error updating user:', error.response ? error.response.data : error.message);
      alert(error.response?.data?.message || 'Failed to update user');
    }
  };

  return (
    <div>
      <h2>Update User</h2>
      <div style={{ marginBottom: '20px' }}>
        <input
          type="text"
          placeholder="Enter User ID"
          onChange={(e) => setUserId(e.target.value)}
          style={{ width: '300px', marginRight: '10px' }}
        />
        <input
          type="text"
          placeholder="Enter JWT Token"
          onChange={(e) => setToken(e.target.value)}
          style={{ width: '300px' }}
        />
        <button
          onClick={fetchUser}
          style={{
            marginLeft: '10px',
            padding: '5px 10px',
            backgroundColor: 'blue',
            color: 'white',
            border: 'none',
            cursor: 'pointer',
          }}
        >
          Load User
        </button>
      </div>

      {isUserLoaded && (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={formData.name}
            onChange={handleChange}
          />
          <input
            type="text"
            name="lastName"
            placeholder="Last Name"
            value={formData.lastName}
            onChange={handleChange}
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
          />
          <input
            type="text"
            name="mobile"
            placeholder="Mobile"
            value={formData.mobile}
            onChange={handleChange}
          />
          <input
            type="text"
            name="userImage"
            placeholder="User Image URL"
            value={formData.userImage}
            onChange={handleChange}
          />
          <button
            type="submit"
            style={{
              marginTop: '10px',
              padding: '5px 10px',
              backgroundColor: 'green',
              color: 'white',
              border: 'none',
              cursor: 'pointer',
            }}
          >
            Update User
          </button>
        </form>
      )}
    </div>
  );
};

export default UpdateUser;
