import React, { useState } from 'react';
import axios from 'axios';

const UpdateUser = () => {
  const [userId, setUserId] = useState('');
  const [formData, setFormData] = useState({});
  const [token, setToken] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`/api/users/${userId}`, formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('User updated successfully');
      console.log(response.data);
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  return (
    <div>
      <h2>Update User</h2>
      <input type="text" placeholder="User ID" onChange={(e) => setUserId(e.target.value)} required />
      <input type="text" name="name" placeholder="Name" onChange={handleChange} />
      <input type="text" name="lastName" placeholder="Last Name" onChange={handleChange} />
      <input type="email" name="email" placeholder="Email" onChange={handleChange} />
      <input type="text" name="mobile" placeholder="Mobile" onChange={handleChange} />
      <input type="text" name="userImage" placeholder="User Image URL" onChange={handleChange} />
      <input
        type="text"
        placeholder="JWT Token"
        onChange={(e) => setToken(e.target.value)}
        style={{ width: '300px', marginBottom: '10px' }}
      />
      <button onClick={handleSubmit}>Update User</button>
    </div>
  );
};

export default UpdateUser;
